import { Component, Inject } from '@angular/core';
import { DOCUMENT } from '@angular/common';

@Component({
  selector: 'app-theme-toggle',
  templateUrl: './theme-toggle.component.html',
  styleUrls: ['./theme-toggle.component.scss'],
})
export class ThemeToggleComponent {
  isDarkThemeActive = false;

  constructor(@Inject(DOCUMENT) private document: Document) {}

  onChange(newValue: boolean): void {
    console.log(newValue);
    this.isDarkThemeActive = newValue;
    if (newValue) {
      this.document.body.classList.remove('light-theme');
      this.document.body.classList.add('dark-theme');
    } else {
      this.document.body.classList.remove('dark-theme');
      this.document.body.classList.add('light-theme');
    }
  }

  toggleTheme(): void {
    this.onChange(!this.isDarkThemeActive);
  }
}
<button
  mat-mini-fab
  style="background-color: white; color: darkgray"
  aria-label="Darkmode/Lightmode toggle"
  (click)="toggleTheme()"
>
  <mat-icon *ngIf="isDarkThemeActive">brightness_2</mat-icon>
  <mat-icon *ngIf="!isDarkThemeActive">brightness_5</mat-icon>
</button>
